def dna(seq):
	minimun_number = 5
	protein_percent = 0.3
	number_nucleotide = 4
	number_per_codon = 3

	name =seq[0] 
	nucleo = seq[-1].upper()
	number_trash = 0
	while '-' in nucleo:
		index = nucleo.find('-')
		nucleo = nucleo[:index] + nucleo[index+1:]
		number_trash += 1

	

	print('Region name: ' + name)
	print('Nucleotides: ' + nucleo)
	print('Nuc. Counts: ' + str(count_number(nucleo)))
	print('Total Mass%: ' + str(mass(nucleo, number_trash)) + ' of ' + str(round(nucleo.count('A') * 135.128 + nucleo.count('C') * 111.103 + nucleo.count('G') * 151.128 + nucleo.count('T') * 125.107 + number_trash * 100.000, 1)))
	print('Codons List: ' + str(codon_list(nucleo)))
	print('Is Protein?:  ' + is_protein(nucleo))

def count_number(nucleo):
	ls = [nucleo.count('A'), nucleo.count('C'), nucleo.count('G'), nucleo.count('T')]
	return ls

def mass(nucleo, number_trash):
	a = nucleo.count('A') * 135.128
	c = nucleo.count('C') * 111.103
	g = nucleo.count('G') * 151.128
	t = nucleo.count('T') * 125.107
	trash = number_trash * 100.000
	totalmass = a + c + g + t + trash
	ls = [round(a*100/totalmass,1), round(c*100/totalmass,1), round(g*100/totalmass,1), round(t*100/totalmass,1)]
	return ls

def codon_list(nucleo):
	ls = []
	for i in range(0, len(nucleo), 3):
		ls.append(nucleo[i:i+3])
	return ls

def is_protein(nucleo):
	if codon_list(nucleo)[0] == 'ATG':
		if codon_list(nucleo[-1] == 'TAA' or "TAG" or 'TGA'):
			if len(codon_list(nucleo)) >= 5:
				if ((nucleo.count('C') * 111.103 + nucleo.count('G') * 151.128)/(nucleo.count('A') * 135.128 + nucleo.count('C') * 111.103 + nucleo.count('G') * 151.128 + nucleo.count('T') * 125.107 + nucleo.count('-') * 100.000) > 0.3):
					return "YES"
	return "NO"


